  def useragent
      @user_agent = request.user_agent
    
      case @user_agent
        when /iPad/i
          @variant = "tablet"
        when /iPhone/i
          @variant = "phone"
        when /Android/i && /mobile/i
          @variant = "phone"
        when /Android/i
          @variant = "tablet"
        when /Windows Phone/i
          @variant = "phone"
        else
          @variant = "desktop"
      end
  end
  
  
